import Card from './components/schoolcard/Card';
import Contacts from './Contacts'
import { useState,useRef,useEffect } from 'react';
import axios from 'axios' 
function createCard(Contacts) {
  const [data, setData]=useState([]);
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/add/listSchool`)
      .then((response) => {
        // console.log(response.data[1].stadium_owned[0].stadium_name);
        setData(response.data);
        console.log(data);
      });
  }, []);


  const ref = useRef(null);
  return (
    <Card
      key={data.id}
      name={Contacts.name}
      img={Contacts.imgURL}
      fees = {Contacts.fees}
      type = {Contacts.type}
      board = {Contacts.board}
      grade= {Contacts.grade}
      add = {Contacts.address}
    />
  );
}

function App1(){
  return (
    <div>
      <h1 className="heading">School List</h1>
      {Contacts.map(createCard)};
    </div>
  );
}

