// import './App.css';
// import School from './components/School'
// import App from './components/schoolcard/App';
import Card from './components/schoolcard/Card';
// import data from './data'
import { useState,useRef,useEffect } from 'react';
import axios from 'axios' 
function CreateCard() {
  const [data, setData]=useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/add/listSchool`)
      .then((response) => {
        // console.log(response.data[1].stadium_owned[0].stadium_name);
        setData(response.data);
        // console.log(data);
      });
  }, []);
const x = data

  const ref = useRef(null);
  return (
    <Card
      key={x._id}
      name={x.name}
      img={x.imgURL}
      fees = {x.fees}
      type = {x.type}
      board = {x.board}
      grade= {x.grade}
      add = {x.address}
    />
  );
}

function App1(){
  return (
    <div>
      <h1 className="heading">School List</h1>
      {x.map(CreateCard)};
    </div>
  );
}

